import React, {useState} from 'react';
import './App.css';

function App() {
  const [showTitle, setShowTitle] = useState(true)

  return (
    <div className="App">
      <header className="App-header">
        <div>
          {showTitle &&
            <h1>Mi lista de mensajes</h1>
          }

        </div>
        <button onClick={() => setShowTitle(false)}>Hide Title</button>
      </header>
    </div>
  );
}

export default App;
